const char *GetTrn(TranslationFile trn)
{
	// clang-format off
	const char *trnFileLoc[] = {
		nullptr,
		"zombie\\bluered",
		"zombie\\grey",
		"zombie\\yellow",
	};
	// clang-format on
	return trnFileLoc[static_cast<uint16_t>(trn)];
}

